-- CREATED TABLES
create table events (
event_id serial primary key,
event_name varchar,
day varchar(10),
month varchar(10),
date numeric(2),
year numeric(4),
start_time varchar(10),
end_time varchar(10),
image text,
address varchar,
city varchar(50),
state varchar(50),
zipcode integer,
details text
);

create table category (
event_id int,
music bool,
sports bool, 
foodie bool, 
indoor bool, 
outdoor bool, 
museum bool, 
art bool, 
book bool, 
lecture bool, 
dance bool,
theatre bool, 
comedy bool,
video bool, 
board bool, 
service bool
);

create table groups (
event_id int,
date bool,
family bool, 
friends bool, 
large bool
)

